     1// Served at /setup/app.js
     2// No fancy syntax: keep it maximally compatible.
     3
     4(function () {
     5  var statusEl = document.getElementById('status');
     6  var statusDetailsEl = document.getElementById('statusDetails');
     7  var authGroupEl = document.getElementById('authGroup');
     8  var authChoiceEl = document.getElementById('authChoice');
     9  var logEl = document.getElementById('log');
    10
    11  // Debug console
    12  var consoleCmdEl = document.getElementById('consoleCmd');
    13  var consoleArgEl = document.getElementById('consoleArg');
    14  var consoleRunEl = document.getElementById('consoleRun');
    15  var consoleOutEl = document.getElementById('consoleOut');
    16
    17  // Config editor
    18  var configPathEl = document.getElementById('configPath');
    19  var configTextEl = document.getElementById('configText');
    20  var configReloadEl = document.getElementById('configReload');
    21  var configSaveEl = document.getElementById('configSave');
    22  var configOutEl = document.getElementById('configOut');
    23
    24  // Import
    25  var importFileEl = document.getElementById('importFile');
    26  var importRunEl = document.getElementById('importRun');
    27  var importOutEl = document.getElementById('importOut');
    28
    29  function setStatus(s) {
    30    statusEl.textContent = s;
    31  }
    32
    33  function isInteractiveOAuth(optionValue, optionLabel) {
    34    var v = String(optionValue || '');
    35    var l = String(optionLabel || '');
    36    return l.indexOf('OAuth') !== -1 || v.indexOf('cli') !== -1 || v.indexOf('codex') !== -1 || v.indexOf('portal') !== -1;
    37  }
    38
    39  function renderAuth(groups) {
    40    authGroupEl.innerHTML = '';
    41
    42    // Toggle for showing interactive OAuth choices.
    43    var advancedToggle = document.getElementById('showAdvancedAuth');
    44    if (!advancedToggle) {
    45      advancedToggle = document.createElement('label');
    46      advancedToggle.style.display = 'block';
    47      advancedToggle.style.marginTop = '0.5rem';
    48      advancedToggle.innerHTML = '<input type="checkbox" id="showAdvancedAuth" /> Show interactive OAuth options (advanced)';
    49      // Insert before authChoiceEl (not its parentNode) to avoid DOM error
    50      authGroupEl.parentNode.insertBefore(advancedToggle, authChoiceEl);
    51    }
    52
    53    for (var i = 0; i < groups.length; i++) {
    54      var g = groups[i];
    55      var opt = document.createElement('option');
    56      opt.value = g.value;
    57      opt.textContent = g.label + (g.hint ? ' - ' + g.hint : '');
    58      authGroupEl.appendChild(opt);
    59    }
    60
    61    function rerenderChoices() {
    62      var sel = null;
    63      for (var j = 0; j < groups.length; j++) {
    64        if (groups[j].value === authGroupEl.value) sel = groups[j];
    65      }
    66      authChoiceEl.innerHTML = '';
    67      var opts = (sel && sel.options) ? sel.options : [];
    68      var showAdv = Boolean(document.getElementById('showAdvancedAuth') && document.getElementById('showAdvancedAuth').checked);
    69
    70      var firstNonInteractive = null;
    71      for (var k = 0; k < opts.length; k++) {
    72        var o = opts[k];
    73        var interactive = isInteractiveOAuth(o.value, o.label);
    74        if (interactive && !showAdv) continue;
    75        if (!interactive && !firstNonInteractive) firstNonInteractive = o.value;
    76
    77        var opt2 = document.createElement('option');
    78        opt2.value = o.value;
    79        opt2.textContent = o.label + (interactive ? ' (interactive OAuth)' : '');
    80        authChoiceEl.appendChild(opt2);
    81      }
    82
    83      // Prefer selecting a non-interactive option by default.
    84      if (firstNonInteractive) authChoiceEl.value = firstNonInteractive;
    85    }
    86
    87    authGroupEl.onchange = rerenderChoices;
    88    var advEl = document.getElementById('showAdvancedAuth');
    89    if (advEl) advEl.onchange = rerenderChoices;
    90
    91    rerenderChoices();
    92  }
    93
    94  function httpJson(url, opts) {
    95    opts = opts || {};
    96    opts.credentials = 'same-origin';
    97    return fetch(url, opts).then(function (res) {
    98      if (!res.ok) {
    99        return res.text().then(function (t) {
   100          throw new Error('HTTP ' + res.status + ': ' + (t || res.statusText));
   101        });
   102      }
   103      return res.json();
   104    });
   105  }
   106
   107  function refreshStatus() {
   108    setStatus('Loading...');
   109    if (statusDetailsEl) statusDetailsEl.textContent = '';
   110
   111    return httpJson('/setup/api/status').then(function (j) {
   112      var ver = j.openclawVersion ? (' | ' + j.openclawVersion) : '';
   113      setStatus((j.configured ? 'Configured' : 'Not configured - run setup below') + ver);
   114
   115      if (statusDetailsEl) {
   116        var parts = [];
   117        parts.push('Gateway target: ' + (j.gatewayTarget || '(unknown)'));
   118        parts.push('Tip: /healthz shows wrapper+gateway reachability.');
   119        statusDetailsEl.textContent = parts.join('\n');
   120      }
   121
   122      // If channels are unsupported, surface it for debugging.
   123      if (j.channelsAddHelp && j.channelsAddHelp.indexOf('telegram') === -1) {
   124        logEl.textContent += '\nNote: this openclaw build does not list telegram in `channels add --help`. Telegram auto-add will be skipped.\n';
   125      }
   126
   127      // Attempt to load config editor content if present.
   128      if (configReloadEl && configTextEl) {
   129        loadConfigRaw();
   130      }
   131
   132    }).catch(function (e) {
   133      setStatus('Error: ' + String(e));
   134      if (statusDetailsEl) statusDetailsEl.textContent = '';
   135    });
   136  }
   137
   138  // Fast auth group load (no subprocesses). Keeps selects from appearing empty.
   139  function loadAuthGroupsFast() {
   140    return httpJson('/setup/api/auth-groups').then(function (j) {
   141      if (j && j.authGroups && j.authGroups.length > 0) {
   142        renderAuth(j.authGroups);
   143        return;
   144      }
   145      throw new Error('Missing authGroups from /setup/api/auth-groups');
   146    }).catch(function (e) {
   147      console.warn('[setup] authGroups load failed:', e);
   148      renderAuth([]);
   149    });
   150  }
   151
   152  document.getElementById('run').onclick = function () {
   153    var payload = {
   154      flow: document.getElementById('flow').value,
   155      authChoice: authChoiceEl.value,
   156      authSecret: document.getElementById('authSecret').value,
   157      telegramToken: document.getElementById('telegramToken').value,
   158      discordToken: document.getElementById('discordToken').value,
   159      slackBotToken: document.getElementById('slackBotToken').value,
   160      slackAppToken: document.getElementById('slackAppToken').value,
   161
   162      customProviderId: document.getElementById('customProviderId').value,
   163      customProviderBaseUrl: document.getElementById('customProviderBaseUrl').value,
   164      customProviderApi: document.getElementById('customProviderApi').value,
   165      customProviderApiKeyEnv: document.getElementById('customProviderApiKeyEnv').value,
   166      customProviderModelId: document.getElementById('customProviderModelId').value
   167    };
   168
   169    logEl.textContent = 'Running...\n';
   170
   171    fetch('/setup/api/run', {
   172      method: 'POST',
   173      credentials: 'same-origin',
   174      headers: { 'content-type': 'application/json' },
   175      body: JSON.stringify(payload)
   176    }).then(function (res) {
   177      return res.text();
   178    }).then(function (text) {
   179      var j;
   180      try { j = JSON.parse(text); } catch (_e) { j = { ok: false, output: text }; }
   181      logEl.textContent += (j.output || JSON.stringify(j, null, 2));
   182      return refreshStatus();
   183    }).catch(function (e) {
   184      logEl.textContent += '\nError: ' + String(e) + '\n';
   185    });
   186  };
   187
   188  // Debug console runner
   189  function runConsole() {
   190    if (!consoleCmdEl || !consoleRunEl) return;
   191    var cmd = consoleCmdEl.value;
   192    var arg = consoleArgEl ? consoleArgEl.value : '';
   193    if (consoleOutEl) consoleOutEl.textContent = 'Running ' + cmd + '...\n';
   194
   195    return httpJson('/setup/api/console/run', {
   196      method: 'POST',
   197      headers: { 'content-type': 'application/json' },
   198      body: JSON.stringify({ cmd: cmd, arg: arg })
   199    }).then(function (j) {
   200      if (consoleOutEl) consoleOutEl.textContent = (j.output || JSON.stringify(j, null, 2));
   201      return refreshStatus();
   202    }).catch(function (e) {
   203      if (consoleOutEl) consoleOutEl.textContent += '\nError: ' + String(e) + '\n';
   204    });
   205  }
   206
   207  if (consoleRunEl) {
   208    consoleRunEl.onclick = runConsole;
   209  }
   210
   211  // Config raw load/save
   212  function loadConfigRaw() {
   213    if (!configTextEl) return;
   214    if (configOutEl) configOutEl.textContent = '';
   215    return httpJson('/setup/api/config/raw').then(function (j) {
   216      if (configPathEl) {
   217        configPathEl.textContent = 'Config file: ' + (j.path || '(unknown)') + (j.exists ? '' : ' (does not exist yet)');
   218      }
   219      configTextEl.value = j.content || '';
   220    }).catch(function (e) {
   221      if (configOutEl) configOutEl.textContent = 'Error loading config: ' + String(e);
   222    });
   223  }
   224
   225  function saveConfigRaw() {
   226    if (!configTextEl) return;
   227    if (!confirm('Save config and restart gateway? A timestamped .bak backup will be created.')) return;
   228    if (configOutEl) configOutEl.textContent = 'Saving...\n';
   229    return httpJson('/setup/api/config/raw', {
   230      method: 'POST',
   231      headers: { 'content-type': 'application/json' },
   232      body: JSON.stringify({ content: configTextEl.value })
   233    }).then(function (j) {
   234      if (configOutEl) configOutEl.textContent = 'Saved: ' + (j.path || '') + '\nGateway restarted.\n';
   235      return refreshStatus();
   236    }).catch(function (e) {
   237      if (configOutEl) configOutEl.textContent += '\nError: ' + String(e) + '\n';
   238    });
   239  }
   240
   241  if (configReloadEl) configReloadEl.onclick = loadConfigRaw;
   242  if (configSaveEl) configSaveEl.onclick = saveConfigRaw;
   243
   244  // Import backup
   245  function runImport() {
   246    if (!importRunEl || !importFileEl) return;
   247    var f = importFileEl.files && importFileEl.files[0];
   248    if (!f) {
   249      alert('Pick a .tar.gz file first');
   250      return;
   251    }
   252    if (!confirm('Import backup? This overwrites files under /data and restarts the gateway.')) return;
   253
   254    if (importOutEl) importOutEl.textContent = 'Uploading ' + f.name + ' (' + f.size + ' bytes)...\n';
   255
   256    return f.arrayBuffer().then(function (buf) {
   257      return fetch('/setup/import', {
   258        method: 'POST',
   259        credentials: 'same-origin',
   260        headers: { 'content-type': 'application/gzip' },
   261        body: buf
   262      });
   263    }).then(function (res) {
   264      return res.text().then(function (t) {
   265        if (importOutEl) importOutEl.textContent += t + '\n';
   266        if (!res.ok) throw new Error('HTTP ' + res.status + ': ' + t);
   267        return refreshStatus();
   268      });
   269    }).catch(function (e) {
   270      if (importOutEl) importOutEl.textContent += '\nError: ' + String(e) + '\n';
   271    });
   272  }
   273
   274  if (importRunEl) importRunEl.onclick = runImport;
   275
   276  // Pairing approve helper
   277  var pairingBtn = document.getElementById('pairingApprove');
   278  if (pairingBtn) {
   279    pairingBtn.onclick = function () {
   280      var channel = prompt('Enter channel (telegram or discord):');
   281      if (!channel) return;
   282      channel = channel.trim().toLowerCase();
   283      if (channel !== 'telegram' && channel !== 'discord') {
   284        alert('Channel must be "telegram" or "discord"');
   285        return;
   286      }
   287      var code = prompt('Enter pairing code (e.g. 3EY4PUYS):');
   288      if (!code) return;
   289      logEl.textContent += '\nApproving pairing for ' + channel + '...\n';
   290      fetch('/setup/api/pairing/approve', {
   291        method: 'POST',
   292        credentials: 'same-origin',
   293        headers: { 'content-type': 'application/json' },
   294        body: JSON.stringify({ channel: channel, code: code.trim() })
   295      }).then(function (r) { return r.text(); })
   296        .then(function (t) { logEl.textContent += t + '\n'; })
   297        .catch(function (e) { logEl.textContent += 'Error: ' + String(e) + '\n'; });
   298    };
   299  }
   300
   301  // Device pairing helper
   302  var devicesRefreshBtn = document.getElementById('devicesRefresh');
   303  var devicesListEl = document.getElementById('devicesList');
   304
   305  function approveDevice(requestId) {
   306    if (!requestId) return;
   307    if (!confirm('Approve device request ' + requestId + '?')) return;
   308    if (devicesListEl) devicesListEl.textContent = 'Approving ' + requestId + '...';
   309
   310    return httpJson('/setup/api/devices/approve', {
   311      method: 'POST',
   312      headers: { 'content-type': 'application/json' },
   313      body: JSON.stringify({ requestId: requestId })
   314    }).then(function (j) {
   315      if (devicesListEl) devicesListEl.textContent = j.output || 'Approved.';
   316      return refreshStatus();
   317    }).catch(function (e) {
   318      if (devicesListEl) devicesListEl.textContent = 'Error: ' + String(e);
   319    });
   320  }
   321
   322  function refreshDevices() {
   323    if (!devicesListEl) return;
   324    devicesListEl.textContent = 'Loading pending devices...';
   325    return httpJson('/setup/api/devices/pending').then(function (j) {
   326      var ids = j.requestIds || [];
   327      if (!ids.length) {
   328        devicesListEl.textContent = 'No pending device requests found.';
   329        return;
   330      }
   331      devicesListEl.innerHTML = '';
   332      for (var i = 0; i < ids.length; i++) {
   333        (function (id) {
   334          var row = document.createElement('div');
   335          row.style.marginTop = '0.25rem';
   336          var btn = document.createElement('button');
   337          btn.textContent = 'Approve ' + id;
   338          btn.style.background = '#111';
   339          btn.style.marginRight = '0.5rem';
   340          btn.onclick = function () { approveDevice(id); };
   341          var code = document.createElement('code');
   342          code.textContent = id;
   343          row.appendChild(btn);
   344          row.appendChild(code);
   345          devicesListEl.appendChild(row);
   346        })(ids[i]);
   347      }
   348    }).catch(function (e) {
   349      devicesListEl.textContent = 'Error: ' + String(e);
   350    });
   351  }
   352
   353  if (devicesRefreshBtn) {
   354    devicesRefreshBtn.onclick = refreshDevices;
   355  }
   356
   357  document.getElementById('reset').onclick = function () {
   358    if (!confirm('Reset setup? This deletes the config file so onboarding can run again.')) return;
   359    logEl.textContent = 'Resetting...\n';
   360    fetch('/setup/api/reset', { method: 'POST', credentials: 'same-origin' })
   361      .then(function (res) { return res.text(); })
   362      .then(function (t) { logEl.textContent += t + '\n'; return refreshStatus(); })
   363      .catch(function (e) { logEl.textContent += 'Error: ' + String(e) + '\n'; });
   364  };
   365
   366  // Populate provider/auth selects ASAP (fast endpoint, no subprocesses)
   367  loadAuthGroupsFast();
   368
   369  // Load the rest of status (version/help) in parallel
   370  refreshStatus();
   371})();
