     1import childProcess from "node:child_process";
     2import crypto from "node:crypto";
     3import fs from "node:fs";
     4import os from "node:os";
     5import path from "node:path";
     6
     7import express from "express";
     8import httpProxy from "http-proxy";
     9import * as tar from "tar";
    10
    11// Migrate deprecated CLAWDBOT_* env vars  OPENCLAW_* so existing Railway deployments
    12// keep working. Users should update their Railway Variables to use the new names.
    13for (const suffix of ["PUBLIC_PORT", "STATE_DIR", "WORKSPACE_DIR", "GATEWAY_TOKEN", "CONFIG_PATH"]) {
    14  const oldKey = `CLAWDBOT_${suffix}`;
    15  const newKey = `OPENCLAW_${suffix}`;
    16  if (process.env[oldKey] && !process.env[newKey]) {
    17    process.env[newKey] = process.env[oldKey];
    18    // Best-effort compatibility shim for old Railway templates.
    19    // Intentionally no warning: Railway templates can still set legacy keys and warnings are noisy.
    20  }
    21  // Avoid forwarding legacy variables into OpenClaw subprocesses.
    22  // OpenClaw logs a warning when deprecated CLAWDBOT_* variables are present.
    23  delete process.env[oldKey];
    24}
    25
    26// Railway injects PORT at runtime and routes traffic to that port.
    27// Do not force a different public port in the container image, or the service may
    28// boot but the Railway domain will be routed to a different port.
    29//
    30// OPENCLAW_PUBLIC_PORT is kept as an escape hatch for non-Railway deployments.
    31const PORT = Number.parseInt(process.env.PORT ?? process.env.OPENCLAW_PUBLIC_PORT ?? "3000", 10);
    32
    33// State/workspace
    34// OpenClaw defaults to ~/.openclaw.
    35const STATE_DIR =
    36  process.env.OPENCLAW_STATE_DIR?.trim() ||
    37  path.join(os.homedir(), ".openclaw");
    38
    39const WORKSPACE_DIR =
    40  process.env.OPENCLAW_WORKSPACE_DIR?.trim() ||
    41  path.join(STATE_DIR, "workspace");
    42
    43// Protect /setup with a user-provided password.
    44const SETUP_PASSWORD = process.env.SETUP_PASSWORD?.trim();
    45
    46// Gateway admin token (protects OpenClaw gateway + Control UI).
    47// Must be stable across restarts. If not provided via env, persist it in the state dir.
    48function resolveGatewayToken() {
    49  const envTok = process.env.OPENCLAW_GATEWAY_TOKEN?.trim();
    50  if (envTok) return envTok;
    51
    52  const tokenPath = path.join(STATE_DIR, "gateway.token");
    53  try {
    54    const existing = fs.readFileSync(tokenPath, "utf8").trim();
    55    if (existing) return existing;
    56  } catch {
    57    // ignore
    58  }
    59
    60  const generated = crypto.randomBytes(32).toString("hex");
    61  try {
    62    fs.mkdirSync(STATE_DIR, { recursive: true });
    63    fs.writeFileSync(tokenPath, generated, { encoding: "utf8", mode: 0o600 });
    64  } catch {
    65    // best-effort
    66  }
    67  return generated;
    68}
    69
    70const OPENCLAW_GATEWAY_TOKEN = resolveGatewayToken();
    71process.env.OPENCLAW_GATEWAY_TOKEN = OPENCLAW_GATEWAY_TOKEN;
    72
    73// Where the gateway will listen internally (we proxy to it).
    74const INTERNAL_GATEWAY_PORT = Number.parseInt(process.env.INTERNAL_GATEWAY_PORT ?? "18789", 10);
    75const INTERNAL_GATEWAY_HOST = process.env.INTERNAL_GATEWAY_HOST ?? "127.0.0.1";
    76const GATEWAY_TARGET = `http://${INTERNAL_GATEWAY_HOST}:${INTERNAL_GATEWAY_PORT}`;
    77
    78// Always run the built-from-source CLI entry directly to avoid PATH/global-install mismatches.
    79const OPENCLAW_ENTRY = process.env.OPENCLAW_ENTRY?.trim() || "/openclaw/dist/entry.js";
    80const OPENCLAW_NODE = process.env.OPENCLAW_NODE?.trim() || "node";
    81
    82function clawArgs(args) {
    83  return [OPENCLAW_ENTRY, ...args];
    84}
    85
    86function resolveConfigCandidates() {
    87  const explicit = process.env.OPENCLAW_CONFIG_PATH?.trim();
    88  if (explicit) return [explicit];
    89
    90  return [path.join(STATE_DIR, "openclaw.json")];
    91}
    92
    93function configPath() {
    94  const candidates = resolveConfigCandidates();
    95  for (const candidate of candidates) {
    96    try {
    97      if (fs.existsSync(candidate)) return candidate;
    98    } catch {
    99      // ignore
   100    }
   101  }
   102  // Default to canonical even if it doesn't exist yet.
   103  return candidates[0] || path.join(STATE_DIR, "openclaw.json");
   104}
   105
   106function isConfigured() {
   107  try {
   108    return resolveConfigCandidates().some((candidate) => fs.existsSync(candidate));
   109  } catch {
   110    return false;
   111  }
   112}
   113
   114// One-time migration: rename legacy config files to openclaw.json so existing
   115// deployments that still have the old filename on their volume keep working.
   116(function migrateLegacyConfigFile() {
   117  // If the operator explicitly chose a config path, do not rename files in STATE_DIR.
   118  if (process.env.OPENCLAW_CONFIG_PATH?.trim()) return;
   119
   120  const canonical = path.join(STATE_DIR, "openclaw.json");
   121  if (fs.existsSync(canonical)) return;
   122
   123  for (const legacy of ["clawdbot.json", "moltbot.json"]) {
   124    const legacyPath = path.join(STATE_DIR, legacy);
   125    try {
   126      if (fs.existsSync(legacyPath)) {
   127        fs.renameSync(legacyPath, canonical);
   128        console.log(`[migration] Renamed ${legacy}  openclaw.json`);
   129        return;
   130      }
   131    } catch (err) {
   132      console.warn(`[migration] Failed to rename ${legacy}: ${err}`);
   133    }
   134  }
   135})();
   136
   137let gatewayProc = null;
   138let gatewayStarting = null;
   139
   140// Debug breadcrumbs for common Railway failures (502 / "Application failed to respond").
   141let lastGatewayError = null;
   142let lastGatewayExit = null;
   143let lastDoctorOutput = null;
   144let lastDoctorAt = null;
   145
   146function sleep(ms) {
   147  return new Promise((r) => setTimeout(r, ms));
   148}
   149
   150async function waitForGatewayReady(opts = {}) {
   151  const timeoutMs = opts.timeoutMs ?? 20_000;
   152  const start = Date.now();
   153  while (Date.now() - start < timeoutMs) {
   154    try {
   155      // Try the default Control UI base path, then fall back to root.
   156      const paths = ["/openclaw", "/"];
   157      for (const p of paths) {
   158        try {
   159          const res = await fetch(`${GATEWAY_TARGET}${p}`, { method: "GET" });
   160          // Any HTTP response means the port is open.
   161          if (res) return true;
   162        } catch {
   163          // try next
   164        }
   165      }
   166    } catch {
   167      // not ready
   168    }
   169    await sleep(250);
   170  }
   171  return false;
   172}
   173
   174async function startGateway() {
   175  if (gatewayProc) return;
   176  if (!isConfigured()) throw new Error("Gateway cannot start: not configured");
   177
   178  fs.mkdirSync(STATE_DIR, { recursive: true });
   179  fs.mkdirSync(WORKSPACE_DIR, { recursive: true });
   180
   181  const args = [
   182    "gateway",
   183    "run",
   184    "--bind",
   185    "loopback",
   186    "--port",
   187    String(INTERNAL_GATEWAY_PORT),
   188    "--auth",
   189    "token",
   190    "--token",
   191    OPENCLAW_GATEWAY_TOKEN,
   192  ];
   193
   194  gatewayProc = childProcess.spawn(OPENCLAW_NODE, clawArgs(args), {
   195    stdio: "inherit",
   196    env: {
   197      ...process.env,
   198      OPENCLAW_STATE_DIR: STATE_DIR,
   199      OPENCLAW_WORKSPACE_DIR: WORKSPACE_DIR,
   200    },
   201  });
   202
   203  gatewayProc.on("error", (err) => {
   204    const msg = `[gateway] spawn error: ${String(err)}`;
   205    console.error(msg);
   206    lastGatewayError = msg;
   207    gatewayProc = null;
   208  });
   209
   210  gatewayProc.on("exit", (code, signal) => {
   211    const msg = `[gateway] exited code=${code} signal=${signal}`;
   212    console.error(msg);
   213    lastGatewayExit = { code, signal, at: new Date().toISOString() };
   214    gatewayProc = null;
   215  });
   216}
