   218async function runDoctorBestEffort() {
   219  // Avoid spamming `openclaw doctor` in a crash loop.
   220  const now = Date.now();
   221  if (lastDoctorAt && now - lastDoctorAt < 5 * 60 * 1000) return;
   222  lastDoctorAt = now;
   223
   224  try {
   225    const r = await runCmd(OPENCLAW_NODE, clawArgs(["doctor"]));
   226    const out = redactSecrets(r.output || "");
   227    lastDoctorOutput = out.length > 50_000 ? out.slice(0, 50_000) + "\n... (truncated)\n" : out;
   228  } catch (err) {
   229    lastDoctorOutput = `doctor failed: ${String(err)}`;
   230  }
   231}
   232
   233async function ensureGatewayRunning() {
   234  if (!isConfigured()) return { ok: false, reason: "not configured" };
   235  if (gatewayProc) return { ok: true };
   236  if (!gatewayStarting) {
   237    gatewayStarting = (async () => {
   238      try {
   239        lastGatewayError = null;
   240        await startGateway();
   241        const ready = await waitForGatewayReady({ timeoutMs: 20_000 });
   242        if (!ready) {
   243          throw new Error("Gateway did not become ready in time");
   244        }
   245      } catch (err) {
   246        const msg = `[gateway] start failure: ${String(err)}`;
   247        lastGatewayError = msg;
   248        // Collect extra diagnostics to help users file issues.
   249        await runDoctorBestEffort();
   250        throw err;
   251      }
   252    })().finally(() => {
   253      gatewayStarting = null;
   254    });
   255  }
   256  await gatewayStarting;
   257  return { ok: true };
   258}
   259
   260async function restartGateway() {
   261  if (gatewayProc) {
   262    try {
   263      gatewayProc.kill("SIGTERM");
   264    } catch {
   265      // ignore
   266    }
   267    // Give it a moment to exit and release the port.
   268    await sleep(750);
   269    gatewayProc = null;
   270  }
   271  return ensureGatewayRunning();
   272}
