   274function requireSetupAuth(req, res, next) {
   275  if (!SETUP_PASSWORD) {
   276    return res
   277      .status(500)
   278      .type("text/plain")
   279      .send("SETUP_PASSWORD is not set. Set it in Railway Variables before using /setup.");
   280  }
   281
   282  const header = req.headers.authorization || "";
   283  const [scheme, encoded] = header.split(" ");
   284  if (scheme !== "Basic" || !encoded) {
   285    res.set("WWW-Authenticate", 'Basic realm="OpenClaw Setup"');
   286    return res.status(401).send("Auth required");
   287  }
   288  const decoded = Buffer.from(encoded, "base64").toString("utf8");
   289  const idx = decoded.indexOf(":");
   290  const password = idx >= 0 ? decoded.slice(idx + 1) : "";
   291  if (password !== SETUP_PASSWORD) {
   292    res.set("WWW-Authenticate", 'Basic realm="OpenClaw Setup"');
   293    return res.status(401).send("Invalid password");
   294  }
   295  return next();
   296}
