   305async function probeGateway() {
   306  // Don't assume HTTP  the gateway primarily speaks WebSocket.
   307  // A simple TCP connect check is enough for "is it up".
   308  const net = await import("node:net");
   309
   310  return await new Promise((resolve) => {
   311    const sock = net.createConnection({
   312      host: INTERNAL_GATEWAY_HOST,
   313      port: INTERNAL_GATEWAY_PORT,
   314      timeout: 750,
   315    });
   316
   317    const done = (ok) => {
   318      try { sock.destroy(); } catch {}
   319      resolve(ok);
   320    };
   321
   322    sock.on("connect", () => done(true));
   323    sock.on("timeout", () => done(false));
   324    sock.on("error", () => done(false));
   325  });
   326}
