   598function buildOnboardArgs(payload) {
   599  const args = [
   600    "onboard",
   601    "--non-interactive",
   602    "--accept-risk",
   603    "--json",
   604    "--no-install-daemon",
   605    "--skip-health",
   606    "--workspace",
   607    WORKSPACE_DIR,
   608    // The wrapper owns public networking; keep the gateway internal.
   609    "--gateway-bind",
   610    "loopback",
   611    "--gateway-port",
   612    String(INTERNAL_GATEWAY_PORT),
   613    "--gateway-auth",
   614    "token",
   615    "--gateway-token",
   616    OPENCLAW_GATEWAY_TOKEN,
   617    "--flow",
   618    payload.flow || "quickstart",
   619  ];
   620
   621  if (payload.authChoice) {
   622    args.push("--auth-choice", payload.authChoice);
   623
   624    // Map secret to correct flag for common choices.
   625    const secret = (payload.authSecret || "").trim();
   626    const map = {
   627      "openai-api-key": "--openai-api-key",
   628      "apiKey": "--anthropic-api-key",
   629      "openrouter-api-key": "--openrouter-api-key",
   630      "ai-gateway-api-key": "--ai-gateway-api-key",
   631      "moonshot-api-key": "--moonshot-api-key",
   632      "kimi-code-api-key": "--kimi-code-api-key",
   633      "gemini-api-key": "--gemini-api-key",
   634      "zai-api-key": "--zai-api-key",
   635      "minimax-api": "--minimax-api-key",
   636      "minimax-api-lightning": "--minimax-api-key",
   637      "synthetic-api-key": "--synthetic-api-key",
   638      "opencode-zen": "--opencode-zen-api-key",
   639    };
   640
   641    const flag = map[payload.authChoice];
   642
   643    // If the user picked an API-key auth choice but didn't provide a secret, fail fast.
   644    // Otherwise OpenClaw may fall back to its default auth choice, which looks like the
   645    // wizard "reverted" their selection.
   646    if (flag && !secret) {
   647      throw new Error(`Missing auth secret for authChoice=${payload.authChoice}`);
   648    }
   649
   650    if (flag) {
   651      args.push(flag, secret);
   652    }
   653
   654    if (payload.authChoice === "token") {
   655      // This is the Anthropic setup-token flow.
   656      if (!secret) throw new Error("Missing auth secret for authChoice=token");
   657      args.push("--token-provider", "anthropic", "--token", secret);
   658    }
   659  }
   660
   661  return args;
   662}
