   664function runCmd(cmd, args, opts = {}) {
   665  return new Promise((resolve) => {
   666    const timeoutMs = Number.isFinite(opts.timeoutMs) ? opts.timeoutMs : 120_000;
   667
   668    const proc = childProcess.spawn(cmd, args, {
   669      ...opts,
   670      env: {
   671        ...process.env,
   672        OPENCLAW_STATE_DIR: STATE_DIR,
   673        OPENCLAW_WORKSPACE_DIR: WORKSPACE_DIR,
   674      },
   675    });
   676
   677    let out = "";
   678    proc.stdout?.on("data", (d) => (out += d.toString("utf8")));
   679    proc.stderr?.on("data", (d) => (out += d.toString("utf8")));
   680
   681    let killTimer;
   682    const timer = setTimeout(() => {
   683      try { proc.kill("SIGTERM"); } catch {}
   684      killTimer = setTimeout(() => {
   685        try { proc.kill("SIGKILL"); } catch {}
   686      }, 2_000);
   687      out += `\n[timeout] Command exceeded ${timeoutMs}ms and was terminated.\n`;
   688      resolve({ code: 124, output: out });
   689    }, timeoutMs);
   690
   691    proc.on("error", (err) => {
   692      clearTimeout(timer);
   693      if (killTimer) clearTimeout(killTimer);
   694      out += `\n[spawn error] ${String(err)}\n`;
   695      resolve({ code: 127, output: out });
   696    });
   697
   698    proc.on("close", (code) => {
   699      clearTimeout(timer);
   700      if (killTimer) clearTimeout(killTimer);
   701      resolve({ code: code ?? 0, output: out });
   702    });
   703  });
   704}
