   738
   739  // Optional setup (only after successful onboarding).
   740  if (ok) {
   741    // Ensure gateway token is written into config so the browser UI can authenticate reliably.
   742    // (We also enforce loopback bind since the wrapper proxies externally.)
   743    // IMPORTANT: Set both gateway.auth.token (server-side) and gateway.remote.token (client-side)
   744    // to the same value so the Control UI can connect without "token mismatch" errors.
   745    await runCmd(OPENCLAW_NODE, clawArgs(["config", "set", "gateway.auth.mode", "token"]));
   746    await runCmd(OPENCLAW_NODE, clawArgs(["config", "set", "gateway.auth.token", OPENCLAW_GATEWAY_TOKEN]));
   747    await runCmd(OPENCLAW_NODE, clawArgs(["config", "set", "gateway.remote.token", OPENCLAW_GATEWAY_TOKEN]));
   748    await runCmd(OPENCLAW_NODE, clawArgs(["config", "set", "gateway.bind", "loopback"]));
   749    await runCmd(OPENCLAW_NODE, clawArgs(["config", "set", "gateway.port", String(INTERNAL_GATEWAY_PORT)]));
   750
   751    // Railway runs behind a reverse proxy. Trust loopback as a proxy hop so local client detection
   752    // remains correct when X-Forwarded-* headers are present.
   753    await runCmd(
   754      OPENCLAW_NODE,
   755      clawArgs(["config", "set", "--json", "gateway.trustedProxies", JSON.stringify(["127.0.0.1"]) ]),
   756    );
   757
   758    // Optional: configure a custom OpenAI-compatible provider (base URL) for advanced users.
   759    if (payload.customProviderId?.trim() && payload.customProviderBaseUrl?.trim()) {
   760      const providerId = payload.customProviderId.trim();
   761      const baseUrl = payload.customProviderBaseUrl.trim();
   762      const api = (payload.customProviderApi || "openai-completions").trim();
   763      const apiKeyEnv = (payload.customProviderApiKeyEnv || "").trim();
   764      const modelId = (payload.customProviderModelId || "").trim();
   765
   766      if (!/^[A-Za-z0-9_-]+$/.test(providerId)) {
   767        extra += `\n[custom provider] skipped: invalid provider id (use letters/numbers/_/-)`;
   768      } else if (!/^https?:\/\//.test(baseUrl)) {
   769        extra += `\n[custom provider] skipped: baseUrl must start with http(s)://`;
   770      } else if (api !== "openai-completions" && api !== "openai-responses") {
   771        extra += `\n[custom provider] skipped: api must be openai-completions or openai-responses`;
   772      } else if (apiKeyEnv && !/^[A-Za-z_][A-Za-z0-9_]*$/.test(apiKeyEnv)) {
   773        extra += `\n[custom provider] skipped: invalid api key env var name`;
   774      } else {
   775        const providerCfg = {
   776          baseUrl,
   777          api,
   778          apiKey: apiKeyEnv ? "${" + apiKeyEnv + "}" : undefined,
   779          models: modelId ? [{ id: modelId, name: modelId }] : undefined,
   780        };
   781
   782        // Ensure we merge in this provider rather than replacing other providers.
   783        await runCmd(OPENCLAW_NODE, clawArgs(["config", "set", "models.mode", "merge"]));
   784        const set = await runCmd(
   785          OPENCLAW_NODE,
   786          clawArgs(["config", "set", "--json", `models.providers.${providerId}`, JSON.stringify(providerCfg)]),
   787        );
   788        extra += `\n[custom provider] exit=${set.code} (output ${set.output.length} chars)\n${set.output || "(no output)"}`;
   789      }
   790    }
   791
   792    const channelsHelp = await runCmd(OPENCLAW_NODE, clawArgs(["channels", "add", "--help"]));
   793    const helpText = channelsHelp.output || "";
   794
   795    const supports = (name) => helpText.includes(name);
   796
   797    if (payload.telegramToken?.trim()) {
   798      if (!supports("telegram")) {
   799        extra += "\n[telegram] skipped (this openclaw build does not list telegram in `channels add --help`)\n";
   800      } else {
   801        // Avoid `channels add` here (it has proven flaky across builds); write config directly.
   802        const token = payload.telegramToken.trim();
   803        const cfgObj = {
   804          enabled: true,
   805          dmPolicy: "pairing",
   806          botToken: token,
   807          groupPolicy: "allowlist",
   808          streamMode: "partial",
   809        };
   810        const set = await runCmd(
   811          OPENCLAW_NODE,
   812          clawArgs(["config", "set", "--json", "channels.telegram", JSON.stringify(cfgObj)]),
   813        );
   814        const get = await runCmd(OPENCLAW_NODE, clawArgs(["config", "get", "channels.telegram"]));
   815
   816        // Best-effort: enable the telegram plugin explicitly (some builds require this even when configured).
   817        const plug = await runCmd(OPENCLAW_NODE, clawArgs(["plugins", "enable", "telegram"]));
   818
   819        extra += `\n[telegram config] exit=${set.code} (output ${set.output.length} chars)\n${set.output || "(no output)"}`;
   820        extra += `\n[telegram verify] exit=${get.code} (output ${get.output.length} chars)\n${get.output || "(no output)"}`;
   821        extra += `\n[telegram plugin enable] exit=${plug.code} (output ${plug.output.length} chars)\n${plug.output || "(no output)"}`;
   822      }
   823    }
   824
   825    if (payload.discordToken?.trim()) {
   826      if (!supports("discord")) {
   827        extra += "\n[discord] skipped (this openclaw build does not list discord in `channels add --help`)\n";
   828      } else {
   829        const token = payload.discordToken.trim();
   830        const cfgObj = {
   831          enabled: true,
   832          token,
   833          groupPolicy: "allowlist",
   834          dm: {
   835            policy: "pairing",
   836          },
   837        };
   838        const set = await runCmd(
   839          OPENCLAW_NODE,
   840          clawArgs(["config", "set", "--json", "channels.discord", JSON.stringify(cfgObj)]),
   841        );
   842        const get = await runCmd(OPENCLAW_NODE, clawArgs(["config", "get", "channels.discord"]));
   843        extra += `\n[discord config] exit=${set.code} (output ${set.output.length} chars)\n${set.output || "(no output)"}`;
   844        extra += `\n[discord verify] exit=${get.code} (output ${get.output.length} chars)\n${get.output || "(no output)"}`;
   845      }
   846    }
   847
   848    if (payload.slackBotToken?.trim() || payload.slackAppToken?.trim()) {
   849      if (!supports("slack")) {
   850        extra += "\n[slack] skipped (this openclaw build does not list slack in `channels add --help`)\n";
   851      } else {
   852        const cfgObj = {
   853          enabled: true,
   854          botToken: payload.slackBotToken?.trim() || undefined,
   855          appToken: payload.slackAppToken?.trim() || undefined,
   856        };
   857        const set = await runCmd(
   858          OPENCLAW_NODE,
   859          clawArgs(["config", "set", "--json", "channels.slack", JSON.stringify(cfgObj)]),
   860        );
   861        const get = await runCmd(OPENCLAW_NODE, clawArgs(["config", "get", "channels.slack"]));
   862        extra += `\n[slack config] exit=${set.code} (output ${set.output.length} chars)\n${set.output || "(no output)"}`;
   863        extra += `\n[slack verify] exit=${get.code} (output ${get.output.length} chars)\n${get.output || "(no output)"}`;
   864      }
   865    }
   866
   867    // Apply changes immediately.
   868    await restartGateway();
   869
   870    // Ensure OpenClaw applies any "configured but not enabled" channel/plugin changes.
   871    // This makes Telegram/Discord pairing issues much less "silent".
   872    const fix = await runCmd(OPENCLAW_NODE, clawArgs(["doctor", "--fix"]));
   873    extra += `\n[doctor --fix] exit=${fix.code} (output ${fix.output.length} chars)\n${fix.output || "(no output)"}`;
   874
   875    // Doctor may require a restart depending on changes.
   876    await restartGateway();
   877  }
   878
   879  return respondJson(ok ? 200 : 500, {
   880    ok,
   881    output: `${prefix}${onboard.output}${extra}`,
   882  });
   883  } catch (err) {
   884    console.error("[/setup/api/run] error:", err);
   885    return respondJson(500, { ok: false, output: `Internal error: ${String(err)}` });
   886  }
   887});
