   946
   947function redactSecrets(text) {
   948  if (!text) return text;
   949  // Very small best-effort redaction. (Config paths/values may still contain secrets.)
   950  return String(text)
   951    .replace(/(sk-[A-Za-z0-9_-]{10,})/g, "[REDACTED]")
   952    .replace(/(gho_[A-Za-z0-9_]{10,})/g, "[REDACTED]")
   953    .replace(/(xox[baprs]-[A-Za-z0-9-]{10,})/g, "[REDACTED]")
   954    // Telegram bot tokens look like: 123456:ABCDEF...
   955    .replace(/(\d{5,}:[A-Za-z0-9_-]{10,})/g, "[REDACTED]")
   956    .replace(/(AA[A-Za-z0-9_-]{10,}:\S{10,})/g, "[REDACTED]");
   957}
   958
   959function extractDeviceRequestIds(text) {
   960  const s = String(text || "");
   961  const out = new Set();
   962
   963  for (const m of s.matchAll(/requestId\s*(?:=|:)\s*([A-Za-z0-9_-]{6,})/g)) out.add(m[1]);
   964  for (const m of s.matchAll(/"requestId"\s*:\s*"([A-Za-z0-9_-]{6,})"/g)) out.add(m[1]);
   965
   966  return Array.from(out);
   967}
