   992app.post("/setup/api/console/run", requireSetupAuth, async (req, res) => {
   993  const payload = req.body || {};
   994  const cmd = String(payload.cmd || "").trim();
   995  const arg = String(payload.arg || "").trim();
   996
   997  if (!ALLOWED_CONSOLE_COMMANDS.has(cmd)) {
   998    return res.status(400).json({ ok: false, error: "Command not allowed" });
   999  }
  1000
  1001  try {
  1002    if (cmd === "gateway.restart") {
  1003      await restartGateway();
  1004      return res.json({ ok: true, output: "Gateway restarted (wrapper-managed).\n" });
  1005    }
  1006    if (cmd === "gateway.stop") {
  1007      if (gatewayProc) {
  1008        try { gatewayProc.kill("SIGTERM"); } catch {}
  1009        await sleep(750);
  1010        gatewayProc = null;
  1011      }
  1012      return res.json({ ok: true, output: "Gateway stopped (wrapper-managed).\n" });
  1013    }
  1014    if (cmd === "gateway.start") {
  1015      const r = await ensureGatewayRunning();
  1016      return res.json({ ok: Boolean(r.ok), output: r.ok ? "Gateway started.\n" : `Gateway not started: ${r.reason}\n` });
  1017    }
  1018
  1019    if (cmd === "openclaw.version") {
  1020      const r = await runCmd(OPENCLAW_NODE, clawArgs(["--version"]));
  1021      return res.status(r.code === 0 ? 200 : 500).json({ ok: r.code === 0, output: redactSecrets(r.output) });
  1022    }
  1023    if (cmd === "openclaw.status") {
  1024      const r = await runCmd(OPENCLAW_NODE, clawArgs(["status"]));
  1025      return res.status(r.code === 0 ? 200 : 500).json({ ok: r.code === 0, output: redactSecrets(r.output) });
  1026    }
  1027    if (cmd === "openclaw.health") {
  1028      const r = await runCmd(OPENCLAW_NODE, clawArgs(["health"]));
  1029      return res.status(r.code === 0 ? 200 : 500).json({ ok: r.code === 0, output: redactSecrets(r.output) });
  1030    }
  1031    if (cmd === "openclaw.doctor") {
  1032      const r = await runCmd(OPENCLAW_NODE, clawArgs(["doctor"]));
  1033      return res.status(r.code === 0 ? 200 : 500).json({ ok: r.code === 0, output: redactSecrets(r.output) });
  1034    }
  1035    if (cmd === "openclaw.logs.tail") {
  1036      const lines = Math.max(50, Math.min(1000, Number.parseInt(arg || "200", 10) || 200));
  1037      const r = await runCmd(OPENCLAW_NODE, clawArgs(["logs", "--tail", String(lines)]));
  1038      return res.status(r.code === 0 ? 200 : 500).json({ ok: r.code === 0, output: redactSecrets(r.output) });
  1039    }
  1040    if (cmd === "openclaw.config.get") {
  1041      if (!arg) return res.status(400).json({ ok: false, error: "Missing config path" });
  1042      const r = await runCmd(OPENCLAW_NODE, clawArgs(["config", "get", arg]));
  1043      return res.status(r.code === 0 ? 200 : 500).json({ ok: r.code === 0, output: redactSecrets(r.output) });
  1044    }
  1045
  1046    // Device management commands (for fixing "disconnected (1008): pairing required")
  1047    if (cmd === "openclaw.devices.list") {
  1048      const r = await runCmd(OPENCLAW_NODE, clawArgs(["devices", "list"]));
  1049      return res.status(r.code === 0 ? 200 : 500).json({ ok: r.code === 0, output: redactSecrets(r.output) });
  1050    }
  1051    if (cmd === "openclaw.devices.approve") {
  1052      const requestId = String(arg || "").trim();
  1053      if (!requestId) {
  1054        return res.status(400).json({ ok: false, error: "Missing device request ID" });
  1055      }
  1056      if (!/^[A-Za-z0-9_-]+$/.test(requestId)) {
  1057        return res.status(400).json({ ok: false, error: "Invalid device request ID" });
  1058      }
  1059      const r = await runCmd(OPENCLAW_NODE, clawArgs(["devices", "approve", requestId]));
  1060      return res.status(r.code === 0 ? 200 : 500).json({ ok: r.code === 0, output: redactSecrets(r.output) });
  1061    }
  1062
  1063    // Plugin management commands
  1064    if (cmd === "openclaw.plugins.list") {
  1065      const r = await runCmd(OPENCLAW_NODE, clawArgs(["plugins", "list"]));
  1066      return res.status(r.code === 0 ? 200 : 500).json({ ok: r.code === 0, output: redactSecrets(r.output) });
  1067    }
  1068    if (cmd === "openclaw.plugins.enable") {
  1069      const name = String(arg || "").trim();
  1070      if (!name) return res.status(400).json({ ok: false, error: "Missing plugin name" });
  1071      if (!/^[A-Za-z0-9_-]+$/.test(name)) return res.status(400).json({ ok: false, error: "Invalid plugin name" });
  1072      const r = await runCmd(OPENCLAW_NODE, clawArgs(["plugins", "enable", name]));
  1073      return res.status(r.code === 0 ? 200 : 500).json({ ok: r.code === 0, output: redactSecrets(r.output) });
  1074    }
  1075
  1076    return res.status(400).json({ ok: false, error: "Unhandled command" });
  1077  } catch (err) {
  1078    return res.status(500).json({ ok: false, error: String(err) });
  1079  }
  1080});
