  1185  const stateAbs = path.resolve(STATE_DIR);
  1186  const workspaceAbs = path.resolve(WORKSPACE_DIR);
  1187
  1188  const dataRoot = "/data";
  1189  const underData = (p) => p === dataRoot || p.startsWith(dataRoot + path.sep);
  1190
