    45→function parseArgs(argv) {
    46→  const args = {};
    47→  for (let i = 0; i < argv.length; i += 1) {
    48→    const token = argv[i];
    49→    if (!token.startsWith("--")) continue;
    50→    const key = token.slice(2);
    51→    const next = argv[i + 1];
    52→    if (!next || next.startsWith("--")) {
    53→      args[key] = true;
    54→      continue;
    55→    }
    56→    args[key] = next;
    57→    i += 1;
    58→  }
    59→  return args;
