   102→async function detectNextIteration(auditLogPath, planId) {
   103→  const raw = await readFileIfExists(auditLogPath);
   104→  let maxIteration = 0;
   105→  for (const line of raw.split("\n")) {
   106→    if (!line.includes(`plan_id:${planId}`)) continue;
   107→    const match = line.match(/\biteration:(\d+)/);
   108→    if (!match) continue;
   109→    maxIteration = Math.max(maxIteration, Number(match[1]));
   110→  }
   111→  return maxIteration + 1;
   112→}
