   128→async function runCommand({
   129→  command,
   130→  args,
   131→  env,
   132→  cwd,
   133→  timeoutSeconds,
   134→  outputFile,
   135→}) {
   136→  const start = Date.now();
   137→  await ensureDir(path.dirname(outputFile));
   138→  const out = fs.createWriteStream(outputFile, { flags: "w" });
   139→  let timedOut = false;
   140→  let outputBytes = 0;
   141→  let preview = "";
   142→
   143→  const child = spawn(command, args, { env, cwd, stdio: ["ignore", "pipe", "pipe"] });
   144→  const timer = setTimeout(() => {
   145→    timedOut = true;
   146→    child.kill("SIGTERM");
   147→    setTimeout(() => child.kill("SIGKILL"), 2000).unref();
   148→  }, timeoutSeconds * 1000);
   149→
   150→  const onChunk = (chunk) => {
   151→    const buf = Buffer.isBuffer(chunk) ? chunk : Buffer.from(String(chunk));
   152→    outputBytes += buf.length;
   153→    out.write(buf);
   154→    preview += buf.toString("utf8");
   155→    preview = collectTextPreview(preview);
   156→  };
   157→
   158→  child.stdout.on("data", onChunk);
   159→  child.stderr.on("data", onChunk);
   160→
   161→  const exit = await new Promise((resolve) => {
   162→    child.on("close", (exitCode, signal) => resolve({ exitCode, signal }));
   163→  });
   164→
   165→  clearTimeout(timer);
   166→  await new Promise((resolve) => out.end(resolve));
   167→  const elapsedSeconds = Math.round((Date.now() - start) / 1000);
   168→
   169→  return {
   170→    command,
   171→    args,
   172→    exitCode: exit.exitCode ?? -1,
   173→    signal: exit.signal ?? "",
   174→    timedOut,
   175→    elapsedSeconds,
   176→    outputBytes,
   177→    preview,
   178→  };
   179→}
