   181→function parseFirstJsonObject(text) {
   182→  const line = text
   183→    .split("\n")
   184→    .map((s) => s.trim())
   185→    .find((s) => s.startsWith("{") && s.endsWith("}"));
   186→  if (!line) return null;
   187→  try {
   188→    return JSON.parse(line);
   189→  } catch {
   190→    return null;
   191→  }
   192→}
