   194→function classifyClaudeResult(result) {
   195→  const normalized = result.preview.toLowerCase();
   196→  const parsed = parseFirstJsonObject(result.preview);
   197→
   198→  if (result.timedOut) {
   199→    if (result.outputBytes > 0) return "claude_timeout_partial_output";
   200→    return "claude_timeout_no_output";
   201→  }
   202→
   203→  const parsedResultText =
   204→    parsed && typeof parsed.result === "string" ? parsed.result.toLowerCase() : "";
   205→  const parsedIsError = Boolean(parsed && parsed.is_error === true);
   206→
   207→  if (
   208→    normalized.includes("credit balance is too low") ||
   209→    parsedResultText.includes("credit balance is too low") ||
   210→    parsedResultText.includes("quota")
   211→  ) {
   212→    return "claude_credit_or_quota_failure";
   213→  }
   214→
   215→  if (
   216→    normalized.includes("invalid api key") ||
   217→    normalized.includes("please run /login") ||
   218→    normalized.includes("authentication_error") ||
   219→    parsedResultText.includes("invalid api key") ||
   220→    parsedResultText.includes("please run /login")
   221→  ) {
   222→    return "claude_auth_failure";
   223→  }
   224→
   225→  if (result.exitCode !== 0 || parsedIsError) {
   226→    if (result.outputBytes === 0) return "claude_empty_output_nonzero";
   227→    return "claude_generic_nonzero";
   228→  }
   229→
   230→  if (result.outputBytes === 0) return "claude_empty_output_nonzero";
   231→
   232→  if (
   233→    normalized.includes("approved with mandatory revisions") ||
   234→    normalized.includes("approve with revisions") ||
   235→    normalized.includes("approved_with_revisions") ||
   236→    parsedResultText.includes("approved_with_revisions")
   237→  ) {
   238→    return "approved_with_revisions";
   239→  }
   240→
   241→  if (normalized.includes("approved")) return "approved";
   242→  return "approved_with_revisions";
   243→}
   244→
   245→function classifyGeminiResult(result) {
   246→  const normalized = result.preview.toLowerCase();
   247→
   248→  if (normalized.includes("code assist login required")) return "gemini_auth_required";
   249→  if (normalized.includes("waiting for authentication")) return "gemini_auth_required";
   250→  if (normalized.includes("authentication timed out")) return "gemini_auth_required";
   251→
   252→  if (result.timedOut) return "fallback_failed";
   253→  if (result.exitCode !== 0) return "fallback_failed";
   254→  if (result.outputBytes === 0) return "fallback_failed";
   255→
   256→  if (
   257→    normalized.includes("approved with mandatory revisions") ||
   258→    normalized.includes("approve with revisions") ||
   259→    normalized.includes("approved_with_revisions")
   260→  ) {
   261→    return "approved_with_revisions";
   262→  }
   263→
   264→  if (normalized.includes("approved")) return "approved";
   265→  return "approved_with_revisions";
   266→}
