   309→async function runPreflight({
   310→  homeDir,
   311→  cwd,
   312→  preflightLogPath,
   313→  timeoutSeconds,
   314→  geminiTimeoutSeconds,
   315→  dryRun,
   316→}) {
   317→  const stages = [];
   318→  const env = { ...process.env, HOME: homeDir };
   319→  const stagesToRun = [
   320→    {
   321→      id: "preflight_stage_1_claude_cli",
   322→      command: "claude",
   323→      args: ["--version"],
   324→      timeout: 15,
   325→      classify: (r) => (r.exitCode === 0 ? "pass" : "fail"),
   326→    },
   327→    {
   328→      id: "preflight_stage_2_gemini_cli",
   329→      command: "gemini",
   330→      args: ["--version"],
   331→      timeout: 15,
   332→      classify: (r) => (r.exitCode === 0 ? "pass" : "fail"),
   333→    },
   334→    {
   335→      id: "preflight_stage_3_claude_noninteractive_probe",
   336→      command: "claude",
   337→      args: ["-p", "--output-format", "json", "--max-turns", "1", "Reply with exactly OK"],
   338→      timeout: Math.min(timeoutSeconds, 30),
   339→      classify: (r) => {
   340→        const t = r.preview.toLowerCase();
   341→        if (t.includes("credit balance is too low")) return "credit_blocked";
   342→        if (t.includes("invalid api key") || t.includes("please run /login")) return "auth_required";
   343→        if (r.exitCode === 0) return "pass";
   344→        return "fail";
   345→      },
   346→    },
   347→    {
   348→      id: "preflight_stage_4_gemini_noninteractive_probe",
   349→      command: "gemini",
   350→      args: ["-p", "Reply with exactly OK"],
   351→      timeout: Math.min(geminiTimeoutSeconds, 30),
   352→      classify: (r) => {
   353→        const t = r.preview.toLowerCase();
   354→        if (
   355→          t.includes("code assist login required") ||
   356→          t.includes("waiting for authentication") ||
   357→          t.includes("authentication timed out")
   358→        ) {
   359→          return "auth_required";
   360→        }
   361→        if (r.exitCode === 0) return "pass";
   362→        return "fail";
   363→      },
   364→    },
   365→  ];
   366→
   367→  if (dryRun) {
   368→    for (const stage of stagesToRun) {
   369→      stages.push({
   370→        id: stage.id,
   371→        status: "dry_run",
   372→        exitCode: 0,
   373→        elapsedSeconds: 0,
   374→        detail: `${stage.command} ${stage.args.join(" ")}`,
   375→      });
   376→    }
   377→  } else {
   378→    for (const stage of stagesToRun) {
   379→      const stageOutput = path.join(
   380→        path.dirname(preflightLogPath),
   381→        `${path.basename(preflightLogPath, ".log")}_${stage.id}.txt`,
   382→      );
   383→      const run = await runCommand({
   384→        command: stage.command,
   385→        args: stage.args,
   386→        env,
   387→        cwd,
   388→        timeoutSeconds: stage.timeout,
   389→        outputFile: stageOutput,
   390→      });
   391→      stages.push({
   392→        id: stage.id,
   393→        status: stage.classify(run),
   394→        exitCode: run.exitCode,
   395→        elapsedSeconds: run.elapsedSeconds,
   396→        detail: `${stage.command} ${stage.args.join(" ")}`,
   397→        outputFile: stageOutput,
   398→      });
   399→    }
   400→  }
   401→
   402→  await ensureDir(path.dirname(preflightLogPath));
   403→  const lines = [
   404→    `# Preflight Validation`,
   405→    `timestamp_utc: ${nowIso()}`,
   406→    `timeout_seconds: ${timeoutSeconds}`,
   407→    `gemini_timeout_seconds: ${geminiTimeoutSeconds}`,
   408→    "",
   409→  ];
   410→  for (const stage of stages) {
   411→    lines.push(
   412→      [
   413→        `${stage.id}`,
   414→        `status=${stage.status}`,
   415→        `exit_code=${stage.exitCode}`,
   416→        `elapsed_seconds=${stage.elapsedSeconds}`,
   417→        `detail="${stage.detail}"`,
   418→        stage.outputFile ? `output_file=${stage.outputFile}` : "",
   419→      ]
   420→        .filter(Boolean)
   421→        .join(" | "),
   422→    );
   423→  }
   424→  lines.push("");
   425→  await fsp.writeFile(preflightLogPath, lines.join("\n"), "utf8");
   426→  return stages;
   427→}
   428→
   429→function hasPreflightBlockers(stages) {
   430→  return stages.some((s) =>
   431→    ["fail", "auth_required", "credit_blocked"].includes(String(s.status)),
   432→  );
   433→}
