   439→function claudeAuditCommand({ outputMode, maxTurns }) {
   440→  return `claude -p --output-format ${outputMode} --permission-mode plan --max-turns ${maxTurns} --agents <json> "Review this plan: <PLAN_JSON>"`;
   441→}
   442→
   443→function geminiAuditCommand() {
   444→  return 'gemini -p "You are the fallback plan reviewer... Review this plan: <PLAN_JSON>"';
   445→}
   446→
   447→async function main() {
   448→  const args = parseArgs(process.argv.slice(2));
   449→  if (args.help || args.h) usageAndExit(0);
   450→  if (!args["plan-file"]) usageAndExit(1);
   451→
   452→  const cwd = process.cwd();
   453→  const planFile = path.resolve(cwd, String(args["plan-file"]));
   454→  const planId =
   455→    args["plan-id"] || path.basename(planFile, path.extname(planFile)).replace(/\s+/g, "_");
   456→  const homeDir = path.resolve(cwd, String(args.home || cwd));
   457→  const timeoutSeconds = Number(args["timeout-seconds"] || DEFAULT_TIMEOUT_SECONDS);
   458→  const geminiTimeoutSeconds = Number(
   459→    args["gemini-timeout-seconds"] || DEFAULT_GEMINI_TIMEOUT_SECONDS,
   460→  );
   461→  const maxTurns = Number(args["max-turns"] || DEFAULT_MAX_TURNS);
   462→  const outputMode = String(args["output-mode"] || DEFAULT_OUTPUT_MODE);
   463→  const outputDir = path.resolve(cwd, String(args["output-dir"] || "audit/plan_review_outputs"));
   464→  const auditLog = path.resolve(cwd, String(args["audit-log"] || "audit/plan_reviews.log"));
   465→  const preflightLog =
   466→    args["preflight-log"] ||
   467→    path.join(outputDir, `preflight_validation_${dateStampUTC()}.log`);
   468→  const disableFallback = Boolean(args["no-fallback"]);
   469→  const skipPreflight = Boolean(args["skip-preflight"]);
   470→  const dryRun = Boolean(args["dry-run"]);
   471→
   472→  if (!OUTPUT_MODES.has(outputMode)) {
   473→    console.error(`Unsupported --output-mode: ${outputMode}`);
   474→    process.exit(2);
   475→  }
   476→  if (!(await fsp.stat(planFile).then(() => true).catch(() => false))) {
   477→    console.error(`Plan file not found: ${planFile}`);
   478→    process.exit(2);
   479→  }
   480→
   481→  const planJson = await fsp.readFile(planFile, "utf8");
   482→  const iteration = args.iteration
   483→    ? Number(args.iteration)
   484→    : await detectNextIteration(auditLog, planId);
   485→  const prompt = `Review this plan: ${planJson}`;
   486→  const env = { ...process.env, HOME: homeDir };
   487→
   488→  const claudeArgs = [
   489→    "-p",
   490→    "--output-format",
   491→    outputMode,
   492→    "--permission-mode",
   493→    "plan",
   494→    "--max-turns",
   495→    String(maxTurns),
   496→    "--agents",
   497→    CLAUDE_AGENTS,
   498→  ];
   499→  if (outputMode === "stream-json") {
   500→    claudeArgs.push("--verbose", "--include-partial-messages");
   501→  }
   502→  claudeArgs.push(prompt);
   503→
   504→  const claudeOutput = path.join(outputDir, `${planId}_round${iteration}_claude.json`);
   505→  const geminiOutput = path.join(outputDir, `${planId}_round${iteration + 1}_gemini.txt`);
   506→
   507→  if (!skipPreflight) {
   508→    const stages = await runPreflight({
   509→      homeDir,
   510→      cwd,
   511→      preflightLogPath: preflightLog,
   512→      timeoutSeconds,
   513→      geminiTimeoutSeconds,
   514→      dryRun,
   515→    });
   516→    if (hasPreflightBlockers(stages) && dryRun === false) {
   517→      console.warn(
   518→        `Preflight reported blockers. See ${preflightLog}. Proceeding for audit completeness.`,
   519→      );
   520→    }
   521→  }
   522→
   523→  if (dryRun) {
   524→    console.log(
   525→      JSON.stringify(
   526→        {
   527→          dry_run: true,
   528→          plan_id: planId,
   529→          iteration,
   530→          home: homeDir,
   531→          claude_command: claudeAuditCommand({ outputMode, maxTurns }),
   532→          fallback_enabled: !disableFallback,
   533→          output_mode: outputMode,
   534→          timeout_seconds: timeoutSeconds,
   535→          preflight_log: preflightLog,
   536→          matrix: FALLBACK_DECISION_MATRIX,
   537→        },
   538→        null,
   539→        2,
   540→      ),
   541→    );
   542→    return;
   543→  }
   544→
   545→  const claudeRun = await runCommand({
   546→    command: "claude",
   547→    args: claudeArgs,
   548→    env,
   549→    cwd,
   550→    timeoutSeconds,
   551→    outputFile: claudeOutput,
   552→  });
   553→  const claudeClass = classifyClaudeResult(claudeRun);
   554→  const action = FALLBACK_DECISION_MATRIX[claudeClass] || "fallback_to_gemini";
   555→
   556→  const claudeResultLabel =
   557→    claudeClass === "approved" || claudeClass === "approved_with_revisions"
   558→      ? claudeClass
   559→      : "claude_failed";
   560→
   561→  await appendLine(
   562→    auditLog,
   563→    buildAuditLine({
   564→      timestamp: nowIso(),
   565→      planId,
   566→      reviewer: "claude-plan-reviewer",
   567→      iteration,
   568→      command: claudeAuditCommand({ outputMode, maxTurns }),
   569→      result: claudeResultLabel,
   570→      summary:
   571→        claudeResultLabel === "claude_failed"
   572→          ? `Primary reviewer failed with classification ${claudeClass}.`
   573→          : `Primary reviewer completed with classification ${claudeClass}.`,
   574→      errors: claudeResultLabel === "claude_failed" ? claudeClass : "none",
   575→      exitCode: claudeRun.exitCode,
   576→      timeoutSeconds,
   577→      elapsedSeconds: claudeRun.elapsedSeconds,
   578→      outputFile: path.relative(cwd, claudeOutput),
   579→    }),
   580→  );
   581→
   582→  if (claudeResultLabel === "approved" || claudeResultLabel === "approved_with_revisions") {
   583→    console.log(
   584→      JSON.stringify(
   585→        {
   586→          plan_id: planId,
   587→          iteration,
   588→          result: claudeResultLabel,
   589→          classification: claudeClass,
   590→          output_file: path.relative(cwd, claudeOutput),
